import React, { Component } from 'react';
import PersonViewer from './PersonViewer';

class App extends Component {
  render() {
    if(!this.state.client) {
      return <Text>Loading</Text>
    }
    return (
        <ApolloProvider client={this.state.client}>

      <main>
        <PersonViewer />
      </main>
        </ApolloProvider>
      );
  }
}

export default App;
