import React from 'react';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { Table } from 'reactstrap';

export const GET_PERSONS = gql
`
  query Test {
    {DemoGraph
        {person (
            order_by : {name : asc})
            {name,
            gender,
            height}
        }
    }

  }
`;

export default () => (
    <Query query={GET_PERSONS}>
      {({ loading, data }) => !loading && (
        <Table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Gender</th>
              <th>Height</th>
            </tr>
          </thead>
          <tbody>
            {data.person.map(person => (
              <tr key={person.name}>
                <td>{person.gender}</td>
                <td>{person.height}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Query>
  );
