import React from 'react';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { Table } from 'reactstrap';

export const GET_PERSONS = gql`
  query GetPersons 
        {person (
            order_by : {name : asc})
            {id,
                name,
            gender,
            height}
        }


`;

export default () => (
    <Query query={GET_PERSONS}>
      {({ loading, data , error}) => !loading && (
        <Table>
          <thead>
            <tr>
                <th>id</th>
              <th>name</th>
              <th>gender</th>
              <th>height</th>
            </tr>
          </thead>
          <tbody>
              {console.log(loading)}
              {console.log(error)}
              {console.log(data)}
            {data.DemoGraph.person.map(person => (
              <tr key={person.id}>
                  <td>{person.name}</td>
                <td>{person.gender}</td>
                <td>{person.height}</td>
              </tr>
            ))
            }
          </tbody>
        </Table>
      )}
    </Query>
  );
